import React, { useState, useEffect } from 'react';

const TypedText = ({ strings, speed = 100, delay = 1000, loop = true, className = '' }) => {
  const [displayText, setDisplayText] = useState('');
  const [stringIndex, setStringIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setDisplayText((prevText) => {
        const currentChar = strings[stringIndex][charIndex];
        return prevText + currentChar;
      });

      setCharIndex((prevIndex) => prevIndex + 1);

      if (charIndex === strings[stringIndex].length - 1) {
        setTimeout(() => {
          setDisplayText('');
          setCharIndex(0);
          setStringIndex((prevIndex) => (loop ? (prevIndex + 1) % strings.length : prevIndex));
        }, delay);
      }

      if (charIndex < strings[stringIndex].length - 1) {
        setTimeout(() => {
          setDisplayText((prevText) => prevText.slice(0, -1));
        }, speed);
      }
    }, speed);

    return () => clearTimeout(timeout);
  }, [strings, speed, delay, loop, charIndex, stringIndex]);

  return <span className={className}>{displayText}</span>;
};

export default TypedText;
