import React, { useEffect, useState } from 'react';

const CustomTypewriter = ({ strings, speed = 100, delay = 1000, loop = true }) => {
  const [text, setText] = useState('');
  let stringIndex = 0;
  let charIndex = 0;

  useEffect(() => {
    const timeout = setTimeout(() => {
      const newChar = strings[stringIndex][charIndex];
      setText((prevText) => prevText + newChar);

      charIndex++;

      if (charIndex === strings[stringIndex].length) {
        charIndex = 0;
        stringIndex++;

        if (stringIndex === strings.length) {
          stringIndex = 0;
          if (!loop) return;
        }

        setTimeout(() => {
          setText('');
        }, delay);
      }

      setTimeout(() => {
        setText((prevText) => prevText.slice(0, -1));
      }, speed);
    }, speed);

    return () => clearTimeout(timeout);
  }, [strings, speed, delay, loop, text, stringIndex, charIndex]);

  return <span className="text-white">{text}</span>;
};

export default CustomTypewriter;
