import React, { useEffect, useRef } from 'react';

const TypedText = ({ strings, speed = 100, delay = 1000, loop = true, className = '' }) => {
  const textRef = useRef('');
  const stringIndexRef = useRef(0);
  const charIndexRef = useRef(0);

  useEffect(() => {
    const timeout = setTimeout(() => {
      textRef.current += strings[stringIndexRef.current][charIndexRef.current];
      charIndexRef.current += 1;

      if (charIndexRef.current === strings[stringIndexRef.current].length) {
        setTimeout(() => {
          textRef.current = '';
          charIndexRef.current = 0;
          stringIndexRef.current = (loop ? (stringIndexRef.current + 1) % strings.length : stringIndexRef.current);
        }, delay);
      }

      if (charIndexRef.current < strings[stringIndexRef.current].length) {
        setTimeout(() => {
          textRef.current = textRef.current.slice(0, -1);
        }, speed);
      }
    }, speed);

    return () => clearTimeout(timeout);
  }, [strings, speed, delay, loop]);

  return <span className={className}>{textRef.current}</span>;
};

export default TypedText;
