import React, { useState, useEffect } from 'react';

const TypedText = ({ strings, speed = 100, delay = 1000, loop = true, className = '' }) => {
  const [text, setText] = useState('');
  const [stringIndex, setStringIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setText((prevText) => prevText + strings[stringIndex][charIndex]);
      setCharIndex((prevIndex) => (prevIndex + 1) % strings[stringIndex].length);

      if (charIndex === strings[stringIndex].length - 1) {
        setTimeout(() => {
          setText('');
          setCharIndex(0);
          setStringIndex((prevIndex) => (loop ? (prevIndex + 1) % strings.length : prevIndex));
        }, delay);
      }

      if (charIndex < strings[stringIndex].length - 1) {
        setTimeout(() => {
          setText((prevText) => prevText.slice(0, -1));
        }, speed);
      }
    }, speed);

    return () => clearTimeout(timeout);
  }, [strings, speed, delay, loop, text, stringIndex, charIndex]);

  return <span className={className}>{text}</span>;
};

export default TypedText;
