import React, { useEffect, useState } from 'react';

const useTypewriter = (text, speed) => {
  const [displayText, setDisplayText] = useState('');
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      if (index < text.length) {
        setDisplayText((prevText) => prevText + text[index]);
        setIndex((prevIndex) => prevIndex + 1);
      } else {
        clearInterval(timer);
      }
    }, speed);

    return () => clearInterval(timer);
  }, [text, speed, index]);

  return displayText;
};

const TypedText = ({ strings, speed = 100, delay = 10, loop = true, className = '' }) => {
  const [currentString, setCurrentString] = useState(0);
  const text = strings[currentString];

  const displayText = useTypewriter(text, speed);

  useEffect(() => {
    const interval = setInterval(() => {
      if (displayText.length === text.length) {
        clearInterval(interval);
        setTimeout(() => {
          setCurrentString((prev) => (loop ? (prev + 1) % strings.length : prev));
        }, delay);
      }
    }, speed);
    return () => clearInterval(interval);
  }, [displayText, text, strings, speed, delay, loop]);

  return <span className={className}>{displayText}</span>;
};

export default TypedText;
